Example for using the linalg::gaussNewtonNlls function #include"NumCpp.hpp"

#include<cstdlib>
#include<iostream>

usingFunctionType=std::function<double(constnc::NdArray<double>&,constnc::NdArray<double>&)>;

voidwikipediaExample()
{
//https://en.wikipedia.org/wiki/Gauss%E2%80%93Newton_algorithm

//Inabiologyexperimentstudyingtherelationbetweensubstrateconcentration[S]andreactionratein
//anenzyme-mediatedreaction,thedatainthefollowingtablewereobtained.
nc::NdArray<double>sMeasured={0.038,0.194,0.425,0.626,1.253,2.5,3.74};
nc::NdArray<double>rateMeasured={0.05,0.127,0.094,0.2122,0.2729,0.2665,0.3317};

//Itisdesiredtofindacurve(modelfunction)oftheform
FunctionTypefunction=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoubles=coordinates.at(0);
constdoublebeta1=betas.at(0);
constdoublebeta2=betas.at(1);

return(beta1*s)/(beta2+s);
};

//partialderivativeoffunctionwithrespecttobeta1
FunctionTypedelFdelBeta1=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoubles=coordinates.at(0);
constdoublebeta2=betas.at(1);

returns/(beta2+s);
};

//partialderivativeoffunctionwithrespecttobeta2
FunctionTypedelFdelBeta2=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoubles=coordinates.at(0);
constdoublebeta1=betas.at(0);
constdoublebeta2=betas.at(1);

return-(beta1*s)/nc::square(beta2+s);
};

//startingwiththeinitialestimatesofbeta1Guessandbeta2Guessandcalculatingafter5iterations
constnc::uint32numIterations=5;
constdoublebeta1Guess=0.9;
constdoublebeta2Guess=0.2;

#ifdef__cpp_structured_bindings
auto[betas,rms]=nc::linalg::gaussNewtonNlls(numIterations,sMeasured.transpose(),rateMeasured,
function,{delFdelBeta1,delFdelBeta2},beta1Guess,beta2Guess);
#else
autoresults=nc::linalg::gaussNewtonNlls(numIterations,sMeasured.transpose(),rateMeasured,
function,{delFdelBeta1,delFdelBeta2},beta1Guess,beta2Guess);
auto&betas=results.first;
auto&rms=results.second;
#endif

std::cout<<"==========WikipediaExample==========\n";
std::cout<<"betavalues="<<betas;
std::cout<<"RMS="<<rms<<'\n';
}

voidexponentialExample()
{
//UnitedStatespopulation(inmillions)andthecorrespondingyear:
nc::NdArray<double>year=nc::arange<double>(1.0,9.0);//justusetimepointsratherthantheyear
nc::NdArray<double>population={8.3,11.0,14.7,19.7,26.7,35.2,44.4,55.9};

//Itisdesiredtofindacurve(modelfunction)oftheform
FunctionTypeexponentialFunction=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoublet=coordinates.at(0);
constdoublebeta1=betas.at(0);
constdoublebeta2=betas.at(1);

returnbeta1*nc::exp(beta2*t);
};

//partialderivativeoffunctionwithrespecttobeta1
FunctionTypedelFdelBeta1=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoublet=coordinates.at(0);
constdoublebeta2=betas.at(1);

returnnc::exp(beta2*t);
};

//partialderivativeoffunctionwithrespecttobeta2
FunctionTypedelFdelBeta2=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoublet=coordinates.at(0);
constdoublebeta1=betas.at(0);
constdoublebeta2=betas.at(1);

returnbeta1*t*nc::exp(beta2*t);
};

//startingwiththeinitialestimatesofbeta1Guessandbeta2Guessandcalculatingafter5iterations
constnc::uint32numIterations=5;
constdoublebeta1Guess=6.0;
constdoublebeta2Guess=0.3;

#ifdef__cpp_structured_bindings
auto[betas,rms]=nc::linalg::gaussNewtonNlls(numIterations,year.transpose(),population,
exponentialFunction,{delFdelBeta1,delFdelBeta2},beta1Guess,beta2Guess);
#else
autoresults=nc::linalg::gaussNewtonNlls(numIterations,year.transpose(),population,
exponentialFunction,{delFdelBeta1,delFdelBeta2},beta1Guess,beta2Guess);
auto&betas=results.first;
auto&rms=results.second;
#endif

std::cout<<"==========ExponentialPopulationExample==========\n";
std::cout<<"betavalues="<<betas;
std::cout<<"RMS="<<rms<<'\n';
}

voidsinusoidalExample()
{
//AveragemonthlyhightemperaturesforBatonRouge,LA:
nc::NdArray<double>month=nc::arange<double>(1.0,13.0);
nc::NdArray<double>temperature={61.0,65.0,72.0,78.0,85.0,90.0,92.0,92.0,88.0,81.0,72.0,63.0};

//Itisdesiredtofindacurve(modelfunction)oftheform
FunctionTypesinusodialFunction=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoublet=coordinates.at(0);
constdoublebeta1=betas.at(0);
constdoublebeta2=betas.at(1);
constdoublebeta3=betas.at(2);
constdoublebeta4=betas.at(3);

returnbeta1*nc::sin(beta2*t+beta3)+beta4;
};

//partialderivativeoffunctionwithrespecttobeta1
FunctionTypedelFdelBeta1=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoublet=coordinates.at(0);
constdoublebeta2=betas.at(1);
constdoublebeta3=betas.at(2);

returnnc::sin(beta2*t+beta3);
};

//partialderivativeoffunctionwithrespecttobeta2
FunctionTypedelFdelBeta2=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoublet=coordinates.at(0);
constdoublebeta1=betas.at(0);
constdoublebeta2=betas.at(1);
constdoublebeta3=betas.at(2);

returnbeta1*t*nc::cos(beta2*t+beta3);
};

//partialderivativeoffunctionwithrespecttobeta3
FunctionTypedelFdelBeta3=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constdoublet=coordinates.at(0);
constdoublebeta1=betas.at(0);
constdoublebeta2=betas.at(1);
constdoublebeta3=betas.at(2);

returnbeta1*nc::cos(beta2*t+beta3);
};

//partialderivativeoffunctionwithrespecttobeta4
FunctionTypedelFdelBeta4=[](constnc::NdArray<double>&/*coordinates*/,constnc::NdArray<double>&/*betas*/)->double
{
return1.0;
};

//startingwiththeinitialestimatesandcalculatingafter5iterations
constnc::uint32numIterations=5;
constdoublebeta1Guess=17.0;
constdoublebeta2Guess=0.5;
constdoublebeta3Guess=10.5;
constdoublebeta4Guess=77.0;

#ifdef__cpp_structured_bindings
auto[betas,rms]=nc::linalg::gaussNewtonNlls(numIterations,month.transpose(),temperature,
sinusodialFunction,{delFdelBeta1,delFdelBeta2,delFdelBeta3,delFdelBeta4},
beta1Guess,beta2Guess,beta3Guess,beta4Guess);
#else
autoresults=nc::linalg::gaussNewtonNlls(numIterations,month.transpose(),temperature,
sinusodialFunction,{delFdelBeta1,delFdelBeta2,delFdelBeta3,delFdelBeta4},
beta1Guess,beta2Guess,beta3Guess,beta4Guess);
auto&betas=results.first;
auto&rms=results.second;
#endif

std::cout<<"==========SinusodialTemperatureExample==========\n";
std::cout<<"betavalues="<<betas;
std::cout<<"RMS="<<rms<<'\n';
}

//https://en.wikipedia.org/wiki/Gaussian_function
doublebaseGaussianFunction(constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)
{
constautox=coordinates.at(0);
constautoy=coordinates.at(1);

constautoa=betas.at(0);
constautox0=betas.at(2);
constautoy0=betas.at(3);
constautosigmaX=betas.at(4);
constautosigmaY=betas.at(5);

returna*nc::exp(-(nc::square(x-x0)/(2.0*nc::square(sigmaX))+nc::square(y-y0)/(2.0*nc::square(sigmaY))));
}

//Itisdesiredtofindacurve(modelfunction)oftheform
doublegaussianFunction(constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)
{
constautodcOffset=betas.at(1);
returnbaseGaussianFunction(coordinates,betas)+dcOffset;
}

voidtwoDimensionalGaussianExample()
{
//createsomedatapointstodescribeatwo-dimensionalgaussianfunction
autocoords=nc::arange<double>(-3.0,3.1,0.1);
#ifdef__cpp_structured_bindings
auto[y,x]=nc::meshgrid<double>(coords,coords);
#else
autogrid=nc::meshgrid<double>(coords,coords);
auto&y=grid.first;
auto&x=grid.second;
#endif
autocoordinates=nc::vstack({x.flatten(),y.flatten()}).transpose();

//randomizesometruthvaluesforthemodelparameters
nc::random::seed(666);//settherandomseedforrepeatability

//makesomerandom"truth"parametersforgeneratingdata
constautoaTruth=nc::random::randFloat<double>(900.0,1100.0);
constautodcOffsetTruth=nc::random::randFloat<double>(90.0,100.0);
constautox0Truth=nc::random::randFloat<double>(-0.5,0.5);
constautoy0Truth=nc::random::randFloat<double>(-0.5,0.5);
constautosigmaXTruth=nc::random::randFloat<double>(0.9,1.1);
constautosigmaYTruth=nc::random::randFloat<double>(0.9,1.1);

nc::NdArray<double>betasTruth={aTruth,dcOffsetTruth,x0Truth,y0Truth,sigmaXTruth,sigmaYTruth};

//makesome"measurements"
nc::NdArray<double>measurements(1,coordinates.shape().rows);
constautocSlice=coordinates.cSlice();
for(nc::uint32i=0;i<measurements.size();++i)
{
measurements[i]=gaussianFunction(coordinates(i,cSlice),betasTruth);
}

//addsomenoisetothemeasurements
autonoise=nc::random::randN<double>(measurements.shape())*10.0;//STD=10ofnoise
measurements+=noise;

//partialderivativeofgaussianFunctionwithrespecttoa
FunctionTypedelFdelA=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constautoa=betas.at(0);
returnbaseGaussianFunction(coordinates,betas)/a;
};

//partialderivativeofgaussianFunctionwithrespecttodcOffset
FunctionTypedelFdelDcOffset=[](constnc::NdArray<double>&/*coordinates*/,constnc::NdArray<double>&/*betas*/)->double
{
return1.0;
};

//partialderivativeofgaussianFunctionwithrespecttox0
FunctionTypedelFdelX0=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constautox=coordinates.at(0);

constautox0=betas.at(2);
constautosigmaX=betas.at(4);

return(x-x0)/nc::square(sigmaX)*baseGaussianFunction(coordinates,betas);
};

//partialderivativeofgaussianFunctionwithrespecttoy0
FunctionTypedelFdelY0=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constautoy=coordinates.at(1);

constautoy0=betas.at(3);
constautosigmaY=betas.at(5);

return(y-y0)/nc::square(sigmaY)*baseGaussianFunction(coordinates,betas);
};

//partialderivativeofgaussianFunctionwithrespecttosigmaX
FunctionTypedelFdelSigmaX=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constautox=coordinates.at(0);

constautox0=betas.at(2);
constautosigmaX=betas.at(4);

return(nc::square(x-x0)/nc::power(sigmaX,3))*baseGaussianFunction(coordinates,betas);
};

//partialderivativeofgaussianFunctionwithrespecttosigmaY
FunctionTypedelFdelSigmaY=[](constnc::NdArray<double>&coordinates,constnc::NdArray<double>&betas)->double
{
constautoy=coordinates.at(1);

constautoy0=betas.at(2);
constautosigmaY=betas.at(4);

return(nc::square(y-y0)/nc::power(sigmaY,3))*baseGaussianFunction(coordinates,betas);
};

//startingwiththeinitialestimatesofandcalculatingafter5iterations
constnc::uint32numIterations=5;
constdoubleaGuess=aTruth+nc::random::randN<double>()*5.0;
constdoubledcOffsetGuess=dcOffsetTruth+nc::random::randN<double>()*5.0;
constdoublex0Guess=x0Truth+nc::random::randN<double>()*0.2;
constdoubley0Guess=y0Truth+nc::random::randN<double>()*0.2;
constdoublesigmaXGuess=sigmaXTruth+nc::random::randN<double>()*0.2;
constdoublesigmaYGuess=sigmaYTruth+nc::random::randN<double>()*0.2;

#ifdef__cpp_structured_bindings
auto[betas,rms]=nc::linalg::gaussNewtonNlls(numIterations,coordinates,measurements,
FunctionType(gaussianFunction),{delFdelA,delFdelDcOffset,delFdelX0,delFdelY0,delFdelSigmaX,delFdelSigmaY},
aGuess,dcOffsetGuess,x0Guess,y0Guess,sigmaXGuess,sigmaYGuess);
#else
autoresults=nc::linalg::gaussNewtonNlls(numIterations,coordinates,measurements,
FunctionType(gaussianFunction),{delFdelA,delFdelDcOffset,delFdelX0,delFdelY0,delFdelSigmaX,delFdelSigmaY},
aGuess,dcOffsetGuess,x0Guess,y0Guess,sigmaXGuess,sigmaYGuess);
auto&betas=results.first;
auto&rms=results.second;
#endif

nc::NdArray<double>initialGuess={aGuess,dcOffsetGuess,x0Guess,y0Guess,sigmaXGuess,sigmaYGuess};

std::cout<<"==========2DGaussianExample==========\n";
std::cout<<"truthvalues="<<betasTruth;
std::cout<<"initialguess="<<initialGuess;
std::cout<<"betavalues="<<betas;
std::cout<<"RMS="<<rms<<'\n';
}

